package io.jpress.model;

import io.jboot.db.annotation.Table;
import io.jpress.model.base.BaseWechatMenu;

import java.util.ArrayList;
import java.util.List;

/**
 * Generated by Jboot.
 */
@Table(tableName = "wechat_menu", primaryKey = "id")
public class WechatMenu extends BaseWechatMenu<WechatMenu> {


    /**
     * 是否是顶级菜单
     *
     * @return
     */
    public boolean isTopCategory() {
        return getPid() != null && getPid() == 0;
    }

    private List<WechatMenu> childs;

    public List<WechatMenu> getChilds() {
        return childs;
    }

    public void setChilds(List<WechatMenu> childs) {
        this.childs = childs;
    }

    public void addChild(WechatMenu child) {
        if (childs == null) {
            childs = new ArrayList<>();
        }
        childs.add(child);
    }

    private int layerNO = 0;

    public int getLayerNO() {
        return layerNO;
    }

    public void setLayerNO(int layerNO) {
        this.layerNO = layerNO;
    }

    public String getLayerString() {
        if (layerNO == 0) {
            return "";
        }

        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < layerNO; i++) {
            if (i == 0)
                sb.append("|—");
            else
                sb.append("—");
        }
        return sb.toString();
    }

    public boolean isMyChild(long id) {
        if (childs == null || childs.isEmpty()) {
            return false;
        }

        return isMyChild(childs, id);
    }

    public boolean hasChild() {
        return childs != null && !childs.isEmpty();
    }

    private boolean isMyChild(List<WechatMenu> categories, long id) {
        for (WechatMenu category : categories) {
            if (category.getId() == id) {
                return true;
            }

            if (category.getChilds() != null) {
                boolean isChild = isMyChild(category.getChilds(), id);
                if (isChild) return true;
            }
        }
        return false;
    }

}
